import chalk from 'chalk';
export function successLog(message, ...optionalParams) {
    console.log(chalk.green(message, optionalParams));
}
export function errorLog(message, ...optionalParams) {
    console.log(chalk.red(message, optionalParams));
}
export function infoLog(message, ...optionalParams) {
    console.log(chalk.blue(message, optionalParams));
}
export function warnLog(message, ...optionalParams) {
    console.log(chalk.yellow(message, optionalParams));
}
